// Language to icon key mapping
export const languageIconMap: Record<string, keyof typeof icons | "default"> = {
  // JavaScript variants
  javascript: "js",
  js: "js",
  jsx: "js",

  // TypeScript variants
  typescript: "ts",
  ts: "ts",
  tsx: "tsx",

  // Node
  node: "node",
  nodejs: "node",

  // Styles
  css: "css",
  scss: "scss",
  sass: "scss",
  less: "css",

  // Shell
  bash: "sh",
  sh: "sh",
  shell: "sh",
  zsh: "sh",

  // Markdown
  markdown: "md",
  md: "md",
  mdx: "md",

  // Config files
  yaml: "yaml",
  yml: "yaml",
  json: "json",
  xml: "xml",

  // Images
  svg: "default",

  // Code files
  html: "html",
  vue: "default",
  python: "py",
  py: "py",
  java: "java",
  c: "default",
  cpp: "default",
  go: "default",
  rust: "default",
  php: "default",
  ruby: "default",

  // Default
  text: "text",
  txt: "text",
  plaintext: "text",

  // Type definition files
  "d.ts": "ts",
  types: "type",
}

