import { ToggleButton } from "@choice-ui/toggle-button"
import { useState } from "react"

export default function App() {

  const [controlled, setControlled] = useState(false)
  return (
    <ToggleButton
      aria-label="Toggle button"
      value={controlled}
      onChange={(value) => setControlled(value)}
    >
      {controlled ? <FieldTypeCheckbox /> : <FieldAdd />}
    </ToggleButton>
  )

}

import { ToggleButton } from "@choice-ui/toggle-button"
import { useState } from "react"

export default function App() {

  enum Variant {
    Default = "default",
    Highlight = "highlight",
    Secondary = "secondary",
  }

  enum Size {
    Default = "default",
    Large = "large",
  }

  enum State {
    Active = "active",
    Disabled = "disabled",
    Focused = "focused",
    Rest = "rest",
  }

  enum Event {
    Click = "click",
    Mousedown = "mousedown",
  }

  const [variant, setVariant] = useState<Variant>(Variant.Default)
  const [size, setSize] = useState<Size>(Size.Default)
  const [controlled, setControlled] = useState(false)
  const [event, setEvent] = useState<Event>(Event.Click)
  return (
    <div className="flex flex-col items-start gap-4">
      <div className="flex gap-4">
        <Select
          value={variant}
          onChange={(value) => setVariant(value as Variant)}
        >
          <Select.Trigger>
            {Object.keys(Variant).find((v) => Variant[v as keyof typeof Variant] === variant)}
          </Select.Trigger>
          <Select.Content>
            {Object.entries(Variant).map(([key, value]) => (
              <Select.Item
                key={key}
                value={value}
              >
                {key}
              </Select.Item>
            ))}
          </Select.Content>
        </Select>
        <Select
          value={size}
          onChange={(value) => setSize(value as Size)}
        >
          <Select.Trigger>
            {Object.keys(Size).find((v) => Size[v as keyof typeof Size] === size)}
          </Select.Trigger>
          <Select.Content>
            {Object.entries(Size).map(([key, value]) => (
              <Select.Item
                key={key}
                value={value}
              >
                {key}
              </Select.Item>
            ))}
          </Select.Content>
        </Select>
        <Select
          value={event}
          onChange={(value) => setEvent(value as Event)}
        >
          <Select.Trigger>
            {Object.keys(Event).find((v) => Event[v as keyof typeof Event] === event)}
          </Select.Trigger>
          <Select.Content>
            {Object.entries(Event).map(([key, value]) => (
              <Select.Item
                key={key}
                value={value}
              >
                {key}
              </Select.Item>
            ))}
          </Select.Content>
        </Select>
      </div>

      <div className="grid grid-cols-[auto_1fr] items-center gap-4">
        {Object.values(State).map((state) => (
          <Fragment key={state}>
            <span className="capitalize text-fuchsia-500">{state}</span>
            <ToggleButton
              variant={variant}
              value={controlled}
              onChange={(value) => setControlled(value)}
              size={size}
              active={state === State.Active}
              focused={state === State.Focused}
              disabled={state === State.Disabled}
              event={event}
            >
              {controlled ? <FieldTypeCheckbox /> : <FieldTypeAttachment />}
            </ToggleButton>
          </Fragment>
        ))}
      </div>
    </div>
  )

}

import { ToggleButton } from "@choice-ui/toggle-button"
import { useState } from "react"

export default function App() {

  const [controlled, setControlled] = useState(false)
  return (
    <ToggleButton
      tooltip={{
        content: controlled ? "Current state is correct" : "Current state is wrong",
      }}
      value={controlled}
      onChange={(value) => setControlled(value)}
    >
      {controlled ? <FieldTypeCheckbox /> : <FieldAdd />}
    </ToggleButton>
  )

}
