import { Menus } from "@choice-ui/menus"
import { useState } from "react"

export default function App() {

  const [activeIndex, setActiveIndex] = useState<number | null>(null)

  const options = [
    { label: "Yesterday", value: "1" },
    { label: "Bohemian Rhapsody", value: "2" },
    { label: "Imagine", value: "3" },
    { label: "Hotel California", value: "4" },
    { label: "Stairway to Heaven", value: "5" },
    { label: "Hey Jude", value: "6" },
  ]

  return (
    <Menus className="w-64">
      {options.map((option, index) => (
        <Menus.Item
          key={option.value}
          active={activeIndex === index}
          onMouseEnter={() => setActiveIndex(index)}
          onMouseLeave={() => setActiveIndex(null)}
        >
          <span className="flex-1 truncate">{option.label}</span>
        </Menus.Item>
      ))}
    </Menus>
  )

}

import { Menus } from "@choice-ui/menus"
import { useState, useMemo } from "react"

export default function App() {

  const [activeIndex, setActiveIndex] = useState<number | null>(null)
  const [search, setSearch] = useState("")

  const options = [
    { label: "Yesterday", value: "1" },
    { label: "Bohemian Rhapsody", value: "2" },
    { label: "Imagine", value: "3" },
    { label: "Hotel California", value: "4" },
    { label: "Stairway to Heaven", value: "5" },
    { label: "Hey Jude", value: "6" },
  ]
  const filteredOptions = useMemo(() => {
    return options.filter((option) => option.label.toLowerCase().includes(search.toLowerCase()))
  }, [search])

  return (
    <Menus className="w-64">
      <Menus.Search
        value={search}
        onChange={setSearch}
      />

      <Menus.Divider />

      {filteredOptions.map((option, index) => (
        <Menus.Item
          key={option.value}
          active={activeIndex === index}
          onMouseEnter={() => setActiveIndex(index)}
          onMouseLeave={() => setActiveIndex(null)}
        >
          <span className="flex-1 truncate">{option.label}</span>
        </Menus.Item>
      ))}

      {filteredOptions.length === 0 && (
        <Menus.SearchEmpty onClear={() => setSearch("")}>
          <Search
            className="text-secondary-foreground"
            width={32}
            height={32}
          />
        </Menus.SearchEmpty>
      )}
    </Menus>
  )

}

