import { ColorSolidPaint, ColorPickerPopover, ColorGradientsPaint, ColorImagePaint, useColorPicker } from "@choice-ui/colors"
import { useState, useRef } from "react"

export default function App() {

  const triggerRef = useRef<HTMLDivElement>(null)
  const [open, setOpen] = useState(false)
  const [showColorContrast, setShowColorContrast] = useState(false)
  const [level, setLevel] = useState<CheckColorContrastLevel>("AA")
  const [category, setCategory] = useState<CheckColorContrastCategory>("auto")
  const [colorSpace, setColorSpace] = useState<ChannelFieldSpace>("hex")

  const {
    color,
    alpha,
    pickerType,
    paintsType,
    libraries,
    gradient,
    handlePickerTypeChange,
    handlePaintsTypeChange,
    handleColorChange,
    handleAlphaChange,
    handleGradientChange,
  } = useColorPicker()

  return (
    <>
      <FillInput
        color={color}
        alpha={alpha}
        open={open}
        setOpen={setOpen}
        setPickerType={handlePickerTypeChange}
        setColor={handleColorChange}
        setAlpha={handleAlphaChange}
        paintsType={paintsType}
        libraries={libraries}
        gradient={gradient}
        setGradient={handleGradientChange}
        triggerRef={triggerRef}
      />

      <ColorPickerPopover
        triggerRef={triggerRef}
        pickerType={pickerType}
        onPickerTypeChange={handlePickerTypeChange}
        paintsType={paintsType}
        onPaintsTypeChange={handlePaintsTypeChange}
        solidPaint={
          <ColorSolidPaint
            colorSpace={colorSpace}
            onColorSpaceChange={(value) => setColorSpace(value as ChannelFieldSpace)}
            color={color}
            alpha={alpha}
            onColorChange={handleColorChange}
            onAlphaChange={handleAlphaChange}
            checkColorContrast={{
              showColorContrast,
              level,
              category,
              backgroundColor: { r: 255, g: 255, b: 255 }, // TODO: 背景颜色需要传入
              foregroundColor: color,
              foregroundAlpha: alpha,
              selectedElementType: "graphics", // TODO: 需要传入元素类型
              handleLevelChange: setLevel,
              handleCategoryChange: setCategory,
              handleShowColorContrast: () => setShowColorContrast((prev) => !prev),
            }}
          />
        }
        gradientPaint={
          <ColorGradientsPaint
            colorSpace={colorSpace}
            onColorSpaceChange={(value) => setColorSpace(value as ChannelFieldSpace)}
            gradient={gradient}
            onGradientChange={handleGradientChange}
          />
        }
        imagePaint={<ColorImagePaint />}
        open={open}
        onOpenChange={setOpen}
        placement="left"
        checkColorContrast={{
          showColorContrast,
          level,
          category,
          backgroundColor: { r: 255, g: 255, b: 255 }, // TODO: 背景颜色需要传入
          foregroundColor: color,
          foregroundAlpha: alpha,
          selectedElementType: "graphics", // TODO: 需要传入元素类型
          handleLevelChange: setLevel,
          handleCategoryChange: setCategory,
          handleShowColorContrast: () => setShowColorContrast((prev) => !prev),
        }}
      />
    </>
  )

}

import { ColorSolidPaint, ColorPickerPopover, ColorGradientsPaint, useColorPicker } from "@choice-ui/colors"
import { useState } from "react"

export default function App() {

  const [features, setFeatures] = useState<PickerFeatures>({
    paintsType: true,
    custom: true,
    // libraries: true,
    pickerType: true,
    solid: true,
    gradient: true,
    pattern: true,
    image: true,
    alpha: true,
    spaceDropdown: true,
    hex: true,
    rgb: true,
    hsl: true,
    hsb: true,
    presets: true,
    nativePicker: true,
    checkColorContrast: true,
  })

  const [showColorContrast, setShowColorContrast] = useState(false)
  const [level, setLevel] = useState<CheckColorContrastLevel>("AA")
  const [category, setCategory] = useState<CheckColorContrastCategory>("auto")
  const [colorSpace, setColorSpace] = useState<ChannelFieldSpace>("hex")

  const {
    color,
    alpha,
    pickerType,
    paintsType,
    gradient,
    handlePickerTypeChange,
    handlePaintsTypeChange,
    handleColorChange,
    handleAlphaChange,
    handleGradientChange,
  } = useColorPicker()

  return (
    <>
      <SolidBackground
        color={color}
        alpha={alpha}
      />

      <FeaturesController
        features={features}
        setFeatures={setFeatures}
      />

      <ColorPickerPopover
        draggable
        rememberPosition
        features={features}
        pickerType={pickerType}
        onPickerTypeChange={handlePickerTypeChange}
        paintsType={paintsType}
        onPaintsTypeChange={handlePaintsTypeChange}
        solidPaint={
          <ColorSolidPaint
            colorSpace={colorSpace}
            onColorSpaceChange={(value) => setColorSpace(value as ChannelFieldSpace)}
            color={color}
            alpha={alpha}
            onColorChange={handleColorChange}
            onAlphaChange={handleAlphaChange}
          />
        }
        gradientPaint={
          <ColorGradientsPaint
            colorSpace={colorSpace}
            onColorSpaceChange={(value) => setColorSpace(value as ChannelFieldSpace)}
            gradient={gradient}
            onGradientChange={handleGradientChange}
          />
        }
        open={true}
        placement="left"
        checkColorContrast={{
          showColorContrast,
          level,
          category,
          backgroundColor: { r: 255, g: 255, b: 255 }, // TODO: 背景颜色需要传入
          foregroundColor: color,
          foregroundAlpha: alpha,
          selectedElementType: "graphics", // TODO: 需要传入元素类型
          handleLevelChange: setLevel,
          handleCategoryChange: setCategory,
          handleShowColorContrast: () => setShowColorContrast((prev) => !prev),
        }}
      >
        <Popover.Trigger>
          <div className="pointer-events-none absolute left-1/2" />
        </Popover.Trigger>
      </ColorPickerPopover>
    </>
  )

}
