import { ColorSwatch } from "@choice-ui/colors"

export default function App() {
  return (

    <div className="flex flex-col items-start gap-4">
      <div className="grid grid-cols-3 gap-4">
        {/* Linear gradient */}
        <div className="flex flex-col items-center gap-2">
          <ColorSwatch
            gradient={{
              type: "GRADIENT_LINEAR",
              gradientTransform: [
                [1, 0, 0],
                [0, 1, 0],
              ],
              gradientStops: [
                {
                  id: nanoid(),
                  color: { r: 0, g: 85, b: 255 },
                  position: 0,
                  alpha: 1,
                },
                {
                  id: nanoid(),
                  color: { r: 255, g: 0, b: 85 },
                  position: 1,
                  alpha: 1,
                },
              ],
            }}
            size={48}
            className="rounded-md"
          />
          <span className="text-body-small">Linear Gradient</span>
        </div>

        {/* Radial gradient */}
        <div className="flex flex-col items-center gap-2">
          <ColorSwatch
            gradient={{
              type: "GRADIENT_RADIAL",
              gradientTransform: [
                [1, 0, 0],
                [0, 1, 0],
              ],
              gradientStops: [
                {
                  id: nanoid(),
                  color: { r: 255, g: 85, b: 0 },
                  position: 0,
                  alpha: 1,
                },
                {
                  id: nanoid(),
                  color: { r: 85, g: 0, b: 255 },
                  position: 1,
                  alpha: 1,
                },
              ],
            }}
            size={48}
            className="rounded-md"
          />
          <span className="text-body-small">Radial Gradient</span>
        </div>

        {/* Angular gradient */}
        <div className="flex flex-col items-center gap-2">
          <ColorSwatch
            gradient={{
              type: "GRADIENT_ANGULAR",
              gradientTransform: [
                [1, 0, 0],
                [0, 1, 0],
              ],
              gradientStops: [
                {
                  id: nanoid(),
                  color: { r: 85, g: 255, b: 0 },
                  position: 0,
                  alpha: 1,
                },
                {
                  id: nanoid(),
                  color: { r: 0, g: 85, b: 255 },
                  position: 0.5,
                  alpha: 1,
                },
                {
                  id: nanoid(),
                  color: { r: 255, g: 0, b: 85 },
                  position: 1,
                  alpha: 1,
                },
              ],
            }}
            size={48}
            className="rounded-md"
          />
          <span className="text-body-small">Angular Gradient</span>
        </div>

        {/* Gradient with transparency */}
        <div className="flex flex-col items-center gap-2">
          <ColorSwatch
            gradient={{
              type: "GRADIENT_LINEAR",
              gradientTransform: [
                [1, 0, 0],
                [0, 1, 0],
              ],
              gradientStops: [
                {
                  id: nanoid(),
                  color: { r: 0, g: 85, b: 255 },
                  position: 0,
                  alpha: 0,
                },
                {
                  id: nanoid(),
                  color: { r: 0, g: 85, b: 255 },
                  position: 1,
                  alpha: 1,
                },
              ],
            }}
            size={48}
            className="rounded-md"
          />
          <span className="text-body-small">Transparency Gradient</span>
        </div>

        {/* Multi-stop gradient */}
        <div className="flex flex-col items-center gap-2">
          <ColorSwatch
            gradient={{
              type: "GRADIENT_LINEAR",
              gradientTransform: [
                [1, 0, 0],
                [0, 1, 0],
              ],
              gradientStops: [
                {
                  id: nanoid(),
                  color: { r: 255, g: 0, b: 0 },
                  position: 0,
                  alpha: 1,
                },
                {
                  id: nanoid(),
                  color: { r: 255, g: 255, b: 0 },
                  position: 0.25,
                  alpha: 1,
                },
                {
                  id: nanoid(),
                  color: { r: 0, g: 255, b: 0 },
                  position: 0.5,
                  alpha: 1,
                },
                {
                  id: nanoid(),
                  color: { r: 0, g: 0, b: 255 },
                  position: 0.75,
                  alpha: 1,
                },
                {
                  id: nanoid(),
                  color: { r: 128, g: 0, b: 128 },
                  position: 1,
                  alpha: 1,
                },
              ],
            }}
            size={48}
            className="rounded-md"
          />
          <span className="text-body-small">Multi-stop Gradient</span>
        </div>
      </div>
      <div className="rounded-md border p-4">
        <ul className="flex flex-col gap-2">
          <li>
            <b className="font-strong">Gradient Types</b>: Linear, Radial, and Angular
          </li>
          <li>
            <b className="font-strong">Gradient Stops</b>: Each stop defines a color, position
            (0-1), and alpha
          </li>
          <li>
            <b className="font-strong">Transform</b>: Controls the direction and orientation of the
            gradient
          </li>
          <li>
            <b className="font-strong">Applications</b>: Useful for representing gradient styles,
            transitions, and multi-color elements
          </li>
        </ul>
      </div>
    </div>

  );
}
