import { Toaster, toast } from "@choice-ui/toast"

export default function App() {

  return (
    <div className="flex flex-col gap-4">
      <p className="text-secondary-foreground">
        Click each button to see the corresponding toast type. Each type has a unique icon and
        semantic meaning.
      </p>
      <div className="flex flex-wrap gap-2">
        <Button
          variant="secondary"
          onClick={() => {
            toast.use("basic")("Default Toast", {
              description: "A general notification without specific semantic meaning.",
            })
          }}
        >
          Default
        </Button>
        <Button
          variant="secondary"
          onClick={() => {
            toast.use("basic").info("Information", {
              description: "Here is some useful information for you.",
            })
          }}
        >
          Info
        </Button>
        <Button
          variant="secondary"
          onClick={() => {
            toast.use("basic").success("Success!", {
              description: "Your changes have been saved successfully.",
            })
          }}
        >
          Success
        </Button>
        <Button
          variant="secondary"
          onClick={() => {
            toast.use("basic").warning("Warning", {
              description: "Please review before proceeding.",
            })
          }}
        >
          Warning
        </Button>
        <Button
          variant="secondary"
          onClick={() => {
            toast.use("basic").error("Error", {
              description: "Something went wrong. Please try again.",
            })
          }}
        >
          Error
        </Button>
        <Button
          variant="secondary"
          onClick={() => {
            toast.use("basic").loading("Loading...", {
              description: "Please wait while we process your request.",
            })
          }}
        >
          Loading
        </Button>
      </div>
      <Toaster id="basic" />
    </div>
  )

}

import { Toaster, toast } from "@choice-ui/toast"

export default function App() {

