import { Toaster, toast } from "@choice-ui/toast"
import { useState, useEffect } from "react"

export default function App() {

  const [isMonitoring, setIsMonitoring] = useState(false)
  const [hasToast, setHasToast] = useState(false)
  const [toastId, setToastId] = useState<string | null>(null)
  const [actionCount, setActionCount] = useState(0)

  // Simulated monitoring effect that triggers toast
  useEffect(() => {
    if (!isMonitoring) return

    const timer = setTimeout(() => {
      if (!hasToast) {
        const id = toast.use("useeffect").warning("System Alert", {
          description: "An issue was detected. Take action or dismiss.",
          duration: 0,
          action: {
            label: `Fix Issue (${actionCount})`,
            onClick: () => {
              setActionCount((prev) => prev + 1)
              setIsMonitoring(false)
              setHasToast(false)
            },
          },
          cancel: {
            label: "Ignore",
            onClick: () => {
              setHasToast(false)
            },
          },
        })

        setToastId(id)
        setHasToast(true)
      }
    }, 1500)

    return () => clearTimeout(timer)
  }, [isMonitoring, hasToast, actionCount])

  // Cleanup effect
  useEffect(() => {
    return () => {
      if (toastId) {
        toast.use("useeffect").dismiss(toastId)
      }
    }
  }, [toastId])

  return (
    <div className="flex flex-col gap-4">
      <p className="text-secondary-foreground">
        This example shows proper toast integration with React effects, including state management
        and cleanup.
      </p>

      <div className="flex gap-2">
        <Button
          variant={isMonitoring ? "primary" : "secondary"}
          onClick={() => {
            setIsMonitoring(!isMonitoring)
            if (isMonitoring && toastId) {
              toast.use("useeffect").dismiss(toastId)
              setHasToast(false)
            }
          }}
        >
          {isMonitoring ? "Stop Monitoring" : "Start Monitoring"}
        </Button>

        <Button
          variant="secondary"
          onClick={() => {
            setActionCount(0)
            setHasToast(false)
            if (toastId) {
              toast.use("useeffect").dismiss(toastId)
            }
          }}
        >
          Reset State
        </Button>
      </div>

      <div className="text-secondary-foreground text-body-small space-y-1 rounded-lg border p-3">
        <p>
          <strong>Status:</strong> {isMonitoring ? "Monitoring active" : "Monitoring inactive"}
        </p>
        <p>
          <strong>Toast:</strong> {hasToast ? "Visible" : "Hidden"}
        </p>
        <p>
          <strong>Action count:</strong> {actionCount}
        </p>
      </div>

      <Toaster id="useeffect" />
    </div>
  )

}

import { Toaster, toast } from "@choice-ui/toast"
import { useState, useMemo } from "react"

export default function App() {

  const [position, setPosition] = useState<
    "top-left" | "top-center" | "top-right" | "bottom-left" | "bottom-center" | "bottom-right"
  >("bottom-right")

  const [offset, setOffset] = useState<number>(16)
  const [variant, setVariant] = useState<ToastVariant>("default")
  const [type, setType] = useState<ToastType>("default")
  const [showProgress, setShowProgress] = useState<boolean>(false)
  const [layout, setLayout] = useState<"default" | "compact">("default")
  const [showAction, setShowAction] = useState<boolean>(false)
  const [showClose, setShowClose] = useState<boolean>(false)

  const [titleLength, setTitleLength] = useState<"short" | "medium" | "long">("short")
  const [descriptionLength, setDescriptionLength] = useState<
    "none" | "short" | "medium" | "long"
  >("short")

  const title = useMemo(() => {
    if (titleLength === "short") {
      return "Short title"
    } else if (titleLength === "medium") {
      return "Medium title that takes up more space in the toast notification."
    } else {
      return "Long title that is very long and will likely wrap to multiple lines in the toast notification area."
    }
  }, [titleLength])

  const description = useMemo(() => {
    if (descriptionLength === "none") {
      return undefined
    } else if (descriptionLength === "short") {
      return "Short description text."
    } else if (descriptionLength === "medium") {
      return "Medium description that provides more context about the notification."
    } else {
      return "Long description that is very detailed and provides comprehensive information about what happened and what the user should do next."
    }
  }, [descriptionLength])

  const toastOptions = useMemo(
    () => ({
      description,
      variant,
      action: showAction
        ? {
            label: "Action",
            onClick: () => {
              console.log("Action clicked")
            },
          }
        : undefined,
      cancel: showClose
        ? {
            label: "Cancel",
            onClick: () => {
              console.log("Cancel clicked")
            },
          }
        : undefined,
    }),
    [description, showAction, showClose, variant],
  )

  return (
    <div className="flex flex-col gap-4">
      <div className="grid grid-cols-4 gap-4">
        <div>
          <p className="text-body-small mb-2 font-medium">Position</p>
          <RadioGroup
            value={position}
            onChange={(value) =>
              setPosition(
                value as
                  | "top-left"
                  | "top-center"
                  | "top-right"
                  | "bottom-left"
                  | "bottom-center"
                  | "bottom-right",
              )
            }
          >
            {[
              "top-left",
              "top-center",
              "top-right",
              "bottom-left",
              "bottom-center",
              "bottom-right",
            ].map((pos) => (
              <RadioGroup.Item
                key={pos}
                value={pos}
              >
                {pos}
              </RadioGroup.Item>
            ))}
          </RadioGroup>
        </div>

        <div>
          <p className="text-body-small mb-2 font-medium">Variant</p>
          <RadioGroup
            value={variant}
            onChange={(value) => setVariant(value as ToastVariant)}
          >
            {["default", "accent", "success", "warning", "error", "assistive"].map((v) => (
              <RadioGroup.Item
                key={v}
                value={v}
              >
                {v}
              </RadioGroup.Item>
            ))}
          </RadioGroup>
        </div>

        <div>
          <p className="text-body-small mb-2 font-medium">Type</p>
          <RadioGroup
            value={type}
            onChange={(value) => setType(value as ToastType)}
          >
            {["default", "info", "success", "warning", "error", "loading"].map((t) => (
              <RadioGroup.Item
                key={t}
                value={t}
              >
                {t}
              </RadioGroup.Item>
            ))}
          </RadioGroup>
        </div>

        <div>
          <p className="text-body-small mb-2 font-medium">Layout</p>
          <RadioGroup
            value={layout}
            onChange={(value) => setLayout(value as "default" | "compact")}
          >
            {["default", "compact"].map((l) => (
              <RadioGroup.Item
                key={l}
                value={l}
              >
                {l}
              </RadioGroup.Item>
            ))}
          </RadioGroup>
        </div>
      </div>

      <div className="flex items-center gap-4">
        <Switch
          size="small"
          value={showProgress}
          onChange={(value) => setShowProgress(value)}
        >
          Progress Bar
        </Switch>
        <Switch
          size="small"
          value={showAction}
          onChange={(value) => setShowAction(value)}
        >
          Action Button
        </Switch>
        <Switch
          size="small"
          value={showClose}
          onChange={(value) => setShowClose(value)}
        >
          Cancel Button
        </Switch>
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div>
          <p className="text-body-small mb-2 font-medium">Title Length</p>
          <RadioGroup
            value={titleLength}
            onChange={(value) => setTitleLength(value as "short" | "medium" | "long")}
          >
            {["short", "medium", "long"].map((length) => (
              <RadioGroup.Item
                key={length}
                value={length}
              >
                {length}
              </RadioGroup.Item>
            ))}
          </RadioGroup>
        </div>
        <div>
          <p className="text-body-small mb-2 font-medium">Description Length</p>
          <RadioGroup
            value={descriptionLength}
            onChange={(value) =>
              setDescriptionLength(value as "none" | "short" | "medium" | "long")
            }
          >
            {["none", "short", "medium", "long"].map((length) => (
              <RadioGroup.Item
                key={length}
                value={length}
              >
                {length}
              </RadioGroup.Item>
            ))}
          </RadioGroup>
        </div>
      </div>

      <div className="flex items-center gap-4">
        <div className="flex items-center gap-2">
          <span className="text-body-small">Offset: {offset}px</span>
          <Range
            min={0}
            max={64}
            step={4}
            value={offset}
            onChange={(value) => setOffset(value)}
            className="w-32"
          />
        </div>
        <div className="flex-1" />
        <Button
          onClick={() => {
            const api = toast.use("playground")
            switch (type) {
              case "default":
                api(title, toastOptions)
                break
              case "info":
                api.info(title, toastOptions)
                break
              case "success":
                api.success(title, toastOptions)
                break
              case "warning":
                api.warning(title, toastOptions)
                break
              case "error":
                api.error(title, toastOptions)
                break
              case "loading":
                api.loading(title, toastOptions)
                break
            }
          }}
        >
          Show Toast
        </Button>
      </div>

      <Toaster
        id="playground"
        key={`${position}-${layout}`}
        position={position}
        showProgress={showProgress}
        offset={offset}
        layout={layout}
      />
    </div>
  )

}
