import { useAlertDialog, AlertDialogProvider } from "@choice-ui/alert-dialog"
import { useState } from "react"

export default function App() {

  const ConfirmExample = () => {
    const { confirm } = useAlertDialog()
    const [result, setResult] = useState<boolean | null>(null)

    const handleClick = async () => {
      const confirmed = await confirm("Are you sure you want to delete this item?")
      setResult(confirmed)
    }

    return (
      <div className="flex flex-col gap-4">
        <Button
          variant="secondary"
          onClick={handleClick}
        >
          Show Confirm Dialog
        </Button>
        {result !== null && (
          <p className="text-secondary-foreground text-body-small">
            Result: {result ? "Confirmed" : "Cancelled"}
          </p>
        )}
      </div>
    )
  }

  return (
    <AlertDialogProvider>
      <ConfirmExample />
    </AlertDialogProvider>
  )

}

import { useAlertDialog, AlertDialogProvider } from "@choice-ui/alert-dialog"
import { useState } from "react"

export default function App() {

  const ConfirmExample = () => {
    const { confirm } = useAlertDialog()
    const [result, setResult] = useState<boolean | null>(null)

    const handleSmall = async () => {
      const confirmed = await confirm({
        title: "Small Alert",
        description:
          "This action cannot be undone. This will permanently delete your account and remove your data from our servers.",
        size: "small",
      })
      setResult(confirmed)
    }

    const handleDefault = async () => {
      const confirmed = await confirm({
        title: "Default Alert",
        description:
          "This action cannot be undone. This will permanently delete your account and remove your data from our servers.",
      })
      setResult(confirmed)
    }

    const handleLarge = async () => {
      const confirmed = await confirm({
        title: "Large Alert",
        description:
          "This action cannot be undone. This will permanently delete your account and remove your data from our servers.",
        size: "large",
      })
      setResult(confirmed)
    }

    return (
      <div className="flex flex-col gap-4">
        {result !== null && (
          <p className="text-secondary-foreground">
            Result: {result ? "Confirmed" : "Cancelled"}
          </p>
        )}
        <div className="flex gap-4">
          <Button
            variant="secondary"
            onClick={handleSmall}
          >
            Small
          </Button>
          <Button
            variant="secondary"
            onClick={handleDefault}
          >
            Default
          </Button>
          <Button
            variant="secondary"
            onClick={handleLarge}
          >
            Large
          </Button>
        </div>
      </div>
    )
  }

  return (
    <AlertDialogProvider>
      <ConfirmExample />
    </AlertDialogProvider>
