import { ColorImagePaint, useImageFilterStyle } from "@choice-ui/colors"
import { useState, useMemo } from "react"

export default function App() {

  const [open, setOpen] = useState(false)

  const [image, setImage] = useState<ImagePaint>({
    type: "IMAGE",
    scaleMode: "FILL",
    imageHash: null,
    imageSizes: {
      thumb: "",
      small: "",
      regular: "",
      raw: "",
      sourceSize: {
        width: 0,
        height: 0,
      },
    },
    imageTransform: undefined,
    rotation: 0,
    filters: {
      exposure: 0,
      contrast: 0,
      saturation: 0,
      temperature: 0,
      tint: 0,
    },
  })

  const filterStyle = useImageFilterStyle(image.filters)

  const imgStyle = useMemo(() => {
    const { width, height } = image.imageSizes.sourceSize
    const isLandscape = width > height

    return {
      transform: `rotate(${image.rotation || 0}deg)`,
      aspectRatio: width / height,
      width: isLandscape ? "100vw" : undefined,
      height: isLandscape ? undefined : "100vh",
    }
  }, [image.imageSizes.sourceSize, image.rotation])

  const handleOpenPopover = useEventCallback(() => {
    setOpen(true)
  })

  const handleImageChange = useEventCallback((newImage: ImagePaint) => {
    setImage((prev) => ({
      ...prev,
      ...newImage,
    }))
  })

  return (
    <>
      <div className="ignore pointer-events-none fixed inset-0 grid place-content-center">
        {image.imageHash && (
          <img
            src={image.imageSizes.raw}
            alt="Original"
            className="object-contain"
            style={{
              ...imgStyle,
              ...filterStyle,
            }}
          />
        )}
      </div>

      <IconButton onClick={handleOpenPopover}>
        <SubIndicatorDot />
      </IconButton>

      <Popover
        open={open}
        onOpenChange={setOpen}
        placement="left"
        draggable
        rememberPosition
        outsidePressIgnore="ignore"
      >
        <Popover.Trigger>
          <div className="right-1/10 pointer-events-none absolute" />
        </Popover.Trigger>
        <Popover.Header title="Image Paint" />

        <Popover.Content className="pb-4 pt-0">
          <ColorImagePaint
            image={image}
            onImageChange={handleImageChange}
            onImageChangeEnd={() => {
              console.log("change end")
            }}
            onImageChangeStart={() => {
              console.log("change start")
            }}
          />
        </Popover.Content>
      </Popover>
    </>
  )

}
